                    //print("excess time: " + String(excessTime))
                    wordDictionary[currentWord.lowercased().trimmingCharacters(in: .punctuationCharacters)] = wordSpeed
                } else {
                    excessTime += wordSpeed - (wordDictionary[currentWord.lowercased().trimmingCharacters(in: .punctuationCharacters)] ?? 100)
                    //print("excess time: " + String(excessTime))
                }
            } else {
                wordDictionary[currentWord.lowercased().trimmingCharacters(in: .punctuationCharacters)] = wordSpeed
            }
